import React from 'react';

import Firebase from 'firebase';
import firebaseConfig from '../../services/config';
import GoogleButton from '../../components/GoogleButton.js';
import {authentication} from '../../helpers/authentication.js';
import BVGLogoWithShadow from '../../assets/BVGLogoWithShadow.png';
import { Box } from "@material-ui/core";
import './Login.css';
import bearGif from '../../assets/beargif.gif';
import gorillaGif from '../../assets/gorilla.gif';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';

class App extends React.Component {

    signedIn = true;

    constructor(props) {
        super(props);

        if (!Firebase.apps.length) {
            Firebase.initializeApp(firebaseConfig);
        }
    }

    render() {
        return (
            <BrowserRouter>
                <React.Fragment>
                    <Box 
                        display="flex"
                        justifyContent="center"
                        alignItems="center"
                        minHeight="50vh">
                        <img src={BVGLogoWithShadow} alt="Main Logo"/>                
                    </Box>
                    <Route>
                        <GoogleButton type="button" onClick={() => {
                            console.log('Google Auth Clicked');
                            authentication();   
                        }}> Log in with Google
                        </GoogleButton>  
                    </Route>
                    <div class="gif">
                        <img src={bearGif} onmouseover={bearGif} onmouseout={bearGif} id="beargif" alt="bear"/>
                        <img src={gorillaGif} onmouseover={gorillaGif} onmouseout={gorillaGif} id="gorillagif" alt="gorilla"/>
                    </div>
                </React.Fragment>
            </BrowserRouter>
        );
    }
}

export default App;
