import React from 'react';

import Firebase from 'firebase';
import firebaseConfig from '../../services/config';
import GoogleButton from '../../components/GoogleButton.js';
import {authentication} from '../../helpers/authentication.js';
import BVGLogoWithShadow from '../../assets/BVGLogoWithShadow.png';
import { Box } from "@material-ui/core";
import './Login.css';
import bearGif from '../../assets/beargif.gif';
import gorillaGif from '../../assets/gorilla.gif';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';

class App extends React.Component {

  constructor(props) {
    super(props);

    if (!Firebase.apps.length) { //just a check for no firebase duplicates
    Firebase.initializeApp(firebaseConfig);
    }

    this.state = {
      developers: []
    };
  }


  componentDidUpdate(prevProps, prevState) {
    if (prevState !== this.state) {
      this.writeUserData();
    }
  }

  writeUserData = () => {
    Firebase.database()
      .ref("/")
      .set(this.state);
    console.log("DATA SAVED");
  };


    render() {
        return (
        <React.Fragment>
            <Box 
                display="flex"
                justifyContent="center"
                alignItems="center"
                minHeight="50vh">
                <img src={BVGLogoWithShadow} alt="Main Logo"/>                
            </Box>
            <Router>
                <GoogleButton type="button" onClick={() => {
                    console.log('Google Auth Clicked');
                    authentication();   
                }}> Log in with Google
                </GoogleButton>  
            </Router>
            <div class="gif">
                <img src={bearGif} onmouseover={bearGif} onmouseout={bearGif} id="beargif" alt="bear"/>
                <img src={gorillaGif} onmouseover={gorillaGif} onmouseout={gorillaGif} id="gorillagif" alt="gorilla"/>
            </div>
        </React.Fragment>
        );
    }
}

export default App;
