import Question from './components/Game/Question.js';
import quizQuestions from './components/Game/quizQuestions.js';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
          counter: 0,
          questionId: 1,
          question: '',
          answerOptions: [],
          answer: '',
          answersCount: {},
          result: ''
        };
      }

      componentDidMount() {
        const shuffledAnswerOptions = quizQuestions.map((question) => this.shuffleArray(question.answers));  

