import React from 'react';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';

import Login from './views/Login/Login.js';
import GameSelector from './views/HomePage/GameSelector.js';
import ModeSelector from './views/HomePage/GameSelector.js';

const Wrapper = props => (
    <div style={{ maxWidth: 400, padding: 16, margin: "auto" }} {...props} />
  );

function App() {
    // const login =  2; 
    // switch(login) {
    // case 1:
    //     return <Login />
    // case 2:
    //     return <GameSelector />
    //     default:
    //         return null;
    // }

    return (
        // <Login />  <- Put routing in Login
        <BrowserRouter>
            <Switch>
                <Route
                    path="/"
                    exact
                    render={() => (
                        <Wrapper>
                            <Link to="/login">
                                Login
                            </Link>
                            <Link to="/game">
                                Game
                            </Link>
                        </Wrapper>
                    )}
                />
                <Route
                    path="/login"
                    exact
                    render={() => (
                        <Login />
                    )}
                />
                <Route
                    path="/game"
                    exact
                    render={() => (
                        <GameSelector />
                    )}
                />
            </Switch>
        </BrowserRouter>
    );
}

export default App;
